import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import { Button ,Navbar,Nav ,Container,Row ,Col , Card } from 'react-bootstrap';
import Item from './item'
import data from './data';
import {Routes, Route , Link } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {

  let [shoes] = useState();
  return (
    <div className="App">
        <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
            <Container>
                <Navbar.Brand href="#home">salcho-shop</Navbar.Brand>
                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="responsive-navbar-nav">
                    <Nav className="me-auto">

                    </Nav>
                    <Nav>
                        <Link to="/">HOME</Link>
                        <Link to="/detail">Detail</Link>
                        <Nav.Link href="#deets3">depth3</Nav.Link>
                        <Nav.Link href="#deets4">depth4</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>

        <Routes>
            {/*라우트는 페이지라고 생각하면 된다 .*/}
            <Route path="/" element={<div></div>} />
            <Route path="/detail" element={ <>
                <Container>
                    <Row>
                        {
                            data.map((item,index) => {
                                return (<Item item = {item} key={index}/>)
                            })
                        }
                    </Row>
                </Container>
            </>} />
        </Routes>
        <div className="main-bg" style={{ backgroundImage : 'url(https://unsplash.it/1600/400?cart=9)'}}/>

        <Card className="text-center" bg="dark" >
            <Card.Footer className="text-muted">2 days ago</Card.Footer>
        </Card>
    </div>
  );
}

export default App;
